import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [userInfo, setUserInfo] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // localStorage'dan kullanıcı bilgilerini al
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      setUserInfo(JSON.parse(storedUserInfo));
    } else {
      // Kullanıcı giriş yapmamışsa login sayfasına yönlendir
      navigate('/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    // Kullanıcı bilgilerini localStorage'dan temizle
    localStorage.removeItem('userInfo');
    localStorage.removeItem('token');
    setUserInfo(null);
    navigate('/login');
  };

  if (!userInfo) {
    return (
      <Container className="py-5 text-center">
        <Alert variant="info">
          Yükleniyor...
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="py-5">
      <Row className="mb-4">
        <Col>
          <h1>Hoş Geldiniz, {userInfo.name}!</h1>
          <p>Yemek Rezervasyon Uygulamasına başarıyla giriş yaptınız.</p>
        </Col>
      </Row>

      <Row className="mb-4">
        <Col md={4} className="mb-3">
          <Card className="h-100 shadow-sm">
            <Card.Body>
              <Card.Title>Kullanıcı Bilgileri</Card.Title>
              <hr />
              <p><strong>Ad Soyad:</strong> {userInfo.name}</p>
              <p><strong>E-posta:</strong> {userInfo.email}</p>
              <p><strong>Rol:</strong> {userInfo.role === 'admin' 
                ? 'Yönetici' 
                : userInfo.role === 'vendor' 
                  ? 'Restoran Sahibi' 
                  : 'Kullanıcı'}
              </p>
            </Card.Body>
          </Card>
        </Col>

        <Col md={8}>
          <Card className="shadow-sm">
            <Card.Body>
              <Card.Title>Kontrol Paneli</Card.Title>
              <hr />
              <p>Bu uygulama şu anda minimalist bir yapıda olup, sadece kullanıcı girişi ve kayıt özellikleri içermektedir.</p>
              <p>Uygulamanın tam sürümü geliştirilme aşamasındadır.</p>
              
              <div className="mt-4">
                <Button 
                  variant="danger"
                  onClick={handleLogout}
                  className="me-2"
                >
                  Çıkış Yap
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default HomePage;
